CREATE TABLE Employee
(
	EmpID INT PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	EmpSurname VARCHAR(60) NOT NULL CHECK (EmpSurname ~ '[А-Я][а-я]+'),
	EmpName VARCHAR(60) NOT NULL CHECK (EmpName ~ '[А-Я][а-я]+'),
	EmpPatronymic VARCHAR(60) CHECK (EmpPatronymic ~ '[А-Я][а-я]+' OR (EmpPatronymic  ISNULL)),
	EmpPosition VARCHAR(60) NOT NULL,
	EmpMail VARCHAR(60) NOT NULL UNIQUE CHECK (EmpMail ~ '^[^@]+@[^@]+\.[^@]+$'),
	EmpPassword VARCHAR(60) NOT NULL,
	PositionId INT NOT NULL,
	FOREIGN KEY (PositionId) REFERENCES Position(PositionId) ON DELETE CASCADE;
);

CREATE TABLE TypeTest
(
	TypeTestId INT PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	TypeTestName VARCHAR(60) NOT NULL,
	TypeTestComp VARCHAR(60) NOT NULL,
	TypeTestDesc TEXT,
	SoftTrue BOOLEAN  NOT NULL,
	Position INT,
	FOREIGN KEY (Position) REFERENCES Position(PositionId) ON DELETE CASCADE
);

CREATE TABLE Question
(
	TypeTestID INT NOT NULL,
	Question TEXT NOT NULL,
	VarA TEXT NOT NULL,
	VarB TEXT NOT NULL,
	VarC TEXT NOT NULL,
	VarD TEXT NOT NULL,
	Answer VARCHAR(1) NOT NULL CHECK(Answer ~ '[A-D]'),
	FOREIGN KEY (TypeTestID) REFERENCES TypeTest(TypeTestID) ON DELETE CASCADE
);

CREATE TABLE Results
(
	TestId INT PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	TypeTestId INT NOT NULL,
	EmpID INT NOT NULL,
	Date TIMESTAMP NOT NULL,
	TestResult DECIMAL(3,0) NOT NULL CHECK (TestResult <= 100),
	FOREIGN KEY (TypeTestId) REFERENCES TypeTest(TypeTestId) ON DELETE CASCADE,
	FOREIGN KEY (EmpID) REFERENCES Employee(EmpID) ON DELETE CASCADE
);

CREATE TABLE Interview
(
	InterviewId INT PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	EmpID INT NOT NULL,
	HRID INT NOT NULL,
	Date DATE NOT NULL,
	Comment TEXT,
	FOREIGN KEY (HRID) REFERENCES Employee(EmpID) ON DELETE CASCADE,
	FOREIGN KEY (EmpID) REFERENCES Employee(EmpID) ON DELETE CASCADE
);

CREATE TABLE FeedbackFromRec
(
	EmpId INT NOT NULL,
	Date TIMESTAMP NOT NULL,
	Comment TEXT,
	Grade DECIMAL(1,0) NOT NULL CHECK(Grade<=5),
	FOREIGN KEY (EmpID) REFERENCES Employee(EmpID) ON DELETE CASCADE
);

CREATE TABLE Position
(
	PositionID INT PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	PositionName VARCHAR(50) NOT NULL
);
CREATE TABLE Recomendation
(
	TypeTestId INT NOT NULL PRIMARY KEY,
	Recomendation1 TEXT,
	Recomendation2 TEXT,
	Recomendation3 TEXT,
	FOREIGN KEY (TypeTestId) REFERENCES TypeTest(TypeTestId) ON DELETE CASCADE
);

