INSERT INTO TypeTest (TypeTestName, TypeTestComp, TypeTestDesc, SoftTrue, Position) VALUES
('Тест на на выявление коммунтикатывных навыков', 'Коммуникация', 'Этот тест оценит ваши навыки общения и взаимодействия с другими людьми. Вы узнаете, насколько эффективно вы можете передавать информацию, слушать и понимать собеседника, а также строить доверительные отношения. Результаты помогут выявить сильные и слабые стороны в вашей коммуникации, что позволит улучшить взаимодействие в команде и с клиентами.', true, NULL),
